"use strict";

window.onload = function() {
  var font = ""; 
  
  if (font == "serif") { 
    document.body.style = "font-family:serif;"; 
  } else if (font == "sanserif") { 
    document.body.style = "font-family:sans-serif;"; 
  } else {
    // do nothing
  } 
  
  document.querySelector(".base").innerHTML = "<div class='header'></div><div class='content'></div><div class='end'></div>";
  document.querySelector(".header").innerHTML = "<span class='name'></span><span class='hline'></span>";
  document.querySelector(".name").innerHTML = "<h1></h1>"; 
  document.querySelector(".hline").innerHTML = "<hr/>"; document.querySelector(".content").innerHTML = ""; 
  document.querySelector(".end").innerHTML = "<span class='eline'></span><span class='footertext'></span>"; 
  document.querySelector(".eline").innerHTML = "<hr/>"; 
  document.querySelector(".footertext").innerHTML = "<p></p>"
  
  document.getElementById("base").innerHTML = "<div id='header'></div><div id='content'></div><div id='end'></div>"; 
  document.getElementById("header").innerHTML = "<span id='name'></span><span id='hline'></span>"; 
  document.getElementById("name").innerHTML = "<h1></h1>"; document.getElementById("hline").innerHTML = "<hr/>"; 
  document.getElementById("content").innerHTML = ""; 
  document.getElementById("end").innerHTML = "<span id='eline'></span><span id='footertext'></span>"; 
  document.getElementById("eline").innerHTML = "<hr/>"; document.getElementById("footertext").innerHTML = "<p></p>"; 
  
  var d = new Date(); 
  var y = d.getFullYear(); 
  
  document.querySelector(".year").innerHTML = y; 
  document.querySelector("#year").innerHTML = y; 
  document.querySelector('.frame').innerHTML = "<iframe src='' frameborder='0'></iframe>"; 
  document.querySelector('#frame').innerHTML = "<iframe src='' frameborder='0'></iframe>";
};

function yell(msg) {
  window.alert(msg);
};

function dark() {
  document.body.style = "color:white; background:rgb(18, 18, 18);";
};

function light() {
  document.body.style = "color:black; background:white;";
};

function timedReload() {
  setTimeout("retry()", 5000);
};

function retry() {
  window.location.reload();
};

function exit() {
  const file = "";
  window.close(file);
};

function user(user_id) {
  window.open("/users/" + user_id);
};

function post(post_id) {
  window.open("/posts/" + post_id);
};

function other(id) {
  window.open("/pages/" + id);
};
